import Button from 'react-bootstrap/Button';
import React, { useState, useEffect }from "react";
import { useHistory, useParams } from "react-router-dom";
import axios from "axios";
import Card from 'react-bootstrap/Card';
import ListGroup from "react-bootstrap/ListGroup"
import Modal from "react-bootstrap/Modal";
import ListGroupItem from 'react-bootstrap/esm/ListGroupItem';
import ModalHeader from 'react-bootstrap/esm/ModalHeader';
import { useContext } from "react";
import UserInfo from "../components/UserInfo";

import AuthContext from "../context/AuthContext";

import Form from 'react-bootstrap/Form';
import InputGroup from "react-bootstrap/InputGroup";
import FormControl from "react-bootstrap/FormControl";
import useAxios from "../utils/useAxios";


const BlogForm = () => {

    const history = useHistory();
    const params = useParams();

    const registerCompany = async (newCompany) => {
        return await fetch(API_URL, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({
                "name": String(newCompany.name).trim(),
                "foundation": parseInt(newCompany.foundation),
                "website": String(newCompany.website).trim(),
            })
        });
    };

    const initialState = { id: 0, name: "", foundation: 1950, website: "" };

    const [company, setCompany] = useState(initialState);


    const handleInputChange = (e) => {
        // console.log(e.target.name);
        // console.log(e.target.value);
        setCompany({ ...company, [e.target.name]: e.target.value });
      };

      const handleSubmit = async (e) => {
        e.preventDefault();
        try {
          let res;
          if (!params.id) {
            res = await registerCompany(company);
            const data = await res.json();
            if (data.message === "Success") {
              setCompany(initialState);
            }
          } else {
            await updateCompany(params.id, company);
          }
          history.push("/");
        } catch (error) {
          console.log(error);
        }
      };

      const getCompany = async (companyId) => {
        try {
          const res = await getCompany(companyId);
          const data = await res.json();
          const { name, foundation, website } = data.company;
          setCompany({ name, foundation, website });
        } catch (error) {
          console.log(error);
        }
      };

      useEffect(() => {
        if (params.id) {
          getCompany(params.id);
        }
        // eslint-disable-next-line
      }, []);




    return (
        <div className="col-md-3 mx-auto">
          <h2 className="mb-3 text-center">Company</h2>
          <form onSubmit={handleSubmit}>
            <div className="mb-3">
              <label className="form-label">Name</label>
              <input type="text" name="body" value={blog.body} onChange={handleInputChange} className="form-control" minLength="2" maxLength="50" autoFocus required />
            </div>


            <div className="d-grid gap-2">
              {params.id ? (
                <button type="submit" className="btn btn-block btn-primary">
                  Update
                </button>
              ) : (
                <button type="submit" className="btn btn-block btn-success">
                  Register
                </button>
              )}
            </div>
          </form>
        </div>
    )



}
export default BlogForm;
