import Button from 'react-bootstrap/Button';
import React, { useState } from "react";
import ListGroup from "react-bootstrap/ListGroup"
import FormControl from "react-bootstrap/FormControl";
import Modal from "react-bootstrap/Modal";
import axios from "axios";
import ListGroupItem from 'react-bootstrap/esm/ListGroupItem';
import { FaEdit, FaTrashAlt } from 'react-icons/fa';
import ModalHeader from 'react-bootstrap/esm/ModalHeader';


function Blogs({blogs = [], setBlogs }) {

    const [show, setShow] = useState(false);
    const [record, setRecord] = useState(null);

    const handleUpdate = async (id, value) => {
        return axios.put(`/api/updateBlog/${id}/`, value)
        .then((res) => {
            const { data } = res;
            const newBlogs = blogs.map( b => {
                if (b.id === id){
                    return data;

                }
                return b;
            })
            setBlogs(newBlogs);
        }).catch(() => {
            alert("Algo fue mal")
        })
    }

    const handleSaveChanges = async () => {
        await handleUpdate(record.id, {body: record.body});

        handleClose();
    }

    const handleClose = () => {
        setShow(false);
    }

    const handleChange = (e) => {
    setRecord({
        ...record,
        body: e.target.value
    })
    }

    const handleDelete = (id) => {
        axios.delete(`/api/deleteBlog/${id}`)
        .then(() => {
            const newBlogs = blogs.filter(t => {
                return t.id !== id
            });
            setBlogs(newBlogs);
        }).catch(() => {
            alert("Algo fue mal")
        })
    }


    return (
        <ListGroup>
            {blogs.map( b =>  {
                return <div>

                <ListGroupItem key={b.id} className="d-flex justify-content-between align-items-center">
                {b.body}
                <div>
                <FaEdit 
                onClick={() => {setRecord(b); setShow(true)}}
                style={{cursor: "pointer", marginRigth:"12px"}}/>

                &nbsp; &nbsp;

                <FaTrashAlt 
                onClick={() => {
                    handleDelete(b.id);
                }}
                style={{cursor: "pointer"}}/>
                </div>

            </ListGroupItem>

                <Modal show={show} onHide={handleClose}>

                    <ModalHeader closeButton>
                        <Modal.Title>
                            Editar Post
                        </Modal.Title>
                    </ModalHeader>

                    <Modal.Body>
                        <FormControl value={record ? record.body : ""}
                        onChange={handleChange}/>
                    </Modal.Body>
                    <Modal.Footer>
                        <Button variant='dark' onClick={handleClose}>Cerrar </Button>

                        <Button variant='warning' onClick={handleSaveChanges}>Guardar</Button>
                    </Modal.Footer>
                </Modal>


            </div>
            } )}
        </ListGroup>
    );
}

export default Blogs;
