import { useContext, useState, useEffect } from "react";
import UserInfo from "../components/UserInfo";
import AuthContext from "../context/AuthContext";
import { useParams } from "react-router-dom";
import axios from "axios";

const Home = () => {
  const { user } = useContext(AuthContext);
  let {authTokens, logoutUser} = useContext(AuthContext)
  let [notes, setNotes] = useState([]);
  let [blog, setBlog] = useState([])
  const { id } = useParams();



  useEffect(() => {
    axios.get("http://127.0.0.1:8000/api/getBlog/", 
    {headers: {
        'Content-Type':'application/json',
          'Authorization':'Bearer ' + String(authTokens.access)
        }}
    ).then((res) => {
        setNotes(res.data)
    }).catch(() => {
      alert("Algo fue mal")
    })
  }, [])



  useEffect(() => {
    const fetch = async () => {
      try {
        const { data } = await axios.get(`http://127.0.0.1:8000/api/profile/${id}/`);
        setNotes(data);
      } catch (err) {
        console.error(err);
      }
    };
    fetch();
  }, []);

  return (
    <div>
      <center>
      <p>DashBoard</p>
      <h1>{notes.username}</h1>
      <p>{notes.id}</p>
      </center>

      {notes.map( b =>  {
        return  <div>


        <br></br>
        <Card key={b.user.id}>
      <Card.Header>{b.user}</Card.Header>
      <Card.Body>
        <Card.Title>Titulo Super Especial</Card.Title>
        <Card.Text>
        {b.body}
        </Card.Text>
        {
            user.username === b.user &&
            <>
        <Button variant="dark" onClick={() => {setRecord(b); setShow(true)}}>Editar</Button>
        &nbsp; &nbsp;
        <Button variant="dark" onClick={() => {handleDelete(b.id);}}>Eliminar</Button>
            </>
        }
      </Card.Body>
        </Card>


        <Modal show={show} onHide={handleClose}>

            <ModalHeader closeButton>
                <Modal.Title>
                    Editar Post
                </Modal.Title>
            </ModalHeader>

            <Modal.Body>
                <FormControl value={record ? record.body : ""}
                onChange={handleChange}/>
            </Modal.Body>
            <Modal.Footer>
                <Button variant='dark' onClick={handleClose}>Cerrar </Button>

                <Button variant='warning' onClick={handleSaveChanges}>Guardar</Button>
            </Modal.Footer>
        </Modal>


    </div>
    } )}






    </div>
  );
};

export default Home;
