import React, {useState, useEffect, useContext} from 'react'
import AuthContext from '../context/AuthContext'
import Button from 'react-bootstrap/Button';
import { Link } from "react-router-dom";
import axios from "axios";
import Modal from "react-bootstrap/Modal";
import ListGroupItem from 'react-bootstrap/esm/ListGroupItem';
import ModalHeader from 'react-bootstrap/esm/ModalHeader';
import FormControl from "react-bootstrap/FormControl";



const MiPerfil = () => {
    let [notes, setNotes] = useState([])



    let {authTokens, logoutUser} = useContext(AuthContext)
    let [usuario, setUsuario] = useState([]);
    const { user } = useContext(AuthContext);

    const [show, setShow] = useState(false);
    const [record, setRecord] = useState(null);
    let [profile, setProfile] = useState([])





    useEffect(()=> {
        getNotes()
    }, [])


    let getNotes = async() =>{
        let response = await fetch('http://127.0.0.1:8000/api/userBlogs/', {
            method:'GET',
            headers:{
                'Content-Type':'application/json',
                'Authorization':'Bearer ' + String(authTokens.access)
            }
        })
        let data = await response.json()

        if(response.status === 200){
            setNotes(data)
        }else if(response.statusText === 'Unauthorized'){
            logoutUser()
        }

    }


    useEffect(() => {
        axios.get("http://127.0.0.1:8000/api/getProfile/",
        {headers: {
            'Content-Type':'application/json',
              'Authorization':'Bearer ' + String(authTokens.access)
            }}
        ).then((res) => {
            setUsuario(res.data)
        }).catch(() => {
          alert("Algo fue mal")
        })
      }, [])



      const handleUpdate = async (id, value) => {
        return axios.put(`http://127.0.0.1:8000/api/updateProfile/${id}/`, value 
        ,{headers: {
            'Content-Type':'application/json',
              'Authorization':'Bearer ' + String(authTokens.access)


            }

        })
        .then((res) => {
            const { data } = res;
            const newBlogs = profile.map( b => {
                if (b.id === id){
                    return data;

                }
                return b;
            })
            setProfile(newBlogs);
        }).catch(() => {
            alert("Algo fue mal")
        })
    }

    const handleSaveChanges = async () => {
        await handleUpdate(record.id, {name: record.name});

        handleClose();
    }

    const handleClose = () => {
        setShow(false);
    }

    const handleChange = (e) => {
        setRecord({
            ...record,
            name: e.target.value
        })
        }

/*     const handleChange = (e) => {
        setRecord({
            ...record,
            name: e.target.value,
            last_name: e.target.value,
            bio: e.target.value
        })
        } */




    return (
        <div>

        <p>You are logged to the home page!</p>
            <Button variant='dark' onClick={event =>  window.location.href='/crearPerfil'}>
                    Crear Perfil
             </Button>




            <ul>
                {notes.map(note => (
                    <li key={note.id} >{note.body}</li>
                ))}
            </ul>

        {usuario.map( u =>  {
                return  <div>

          {
            user.username === u.user &&
            <>
            <Link to={`/getProfile/${u.id}`}> Info
              <li>{u.user} | {u.id} </li>
            </Link>
            <Button variant="dark" onClick={() => {setRecord(u); setShow(true)}}>Editar</Button>
            </>
        }



                <Modal show={show} onHide={handleClose}>

                <ModalHeader closeButton>
                    <Modal.Title>
                        Editar Perfil
                    </Modal.Title>
                </ModalHeader>

                <Modal.Body>
                <FormControl value={record ? record.name : ""}
                onChange={handleChange}/>

                </Modal.Body>
                <Modal.Footer>
                    <Button variant='dark' onClick={handleClose}>Cerrar </Button>

                    <Button variant='warning' onClick={handleSaveChanges}>Guardar</Button>
                </Modal.Footer>
                </Modal>
                </div>


})}







        </div>

    )

}

export default MiPerfil
