import React, {useState, useEffect, useContext} from 'react'
import AuthContext from '../context/AuthContext'
import Button from 'react-bootstrap/Button';
import { Link } from "react-router-dom";
import axios from "axios";



const MiPerfil = () => {
    let [notes, setNotes] = useState([])
    let {authTokens, logoutUser} = useContext(AuthContext)
    let [usuario, setUsuario] = useState([]);
    const { user } = useContext(AuthContext);
    const [show, setShow] = useState(false);
    const [record, setRecord] = useState(null);



    useEffect(()=> {
        getNotes()
    }, [])


    let getNotes = async() =>{
        let response = await fetch('http://127.0.0.1:8000/api/userBlogs/', {
            method:'GET',
            headers:{
                'Content-Type':'application/json',
                'Authorization':'Bearer ' + String(authTokens.access)
            }
        })
        let data = await response.json()

        if(response.status === 200){
            setNotes(data)
        }else if(response.statusText === 'Unauthorized'){
            logoutUser()
        }

    }


    useEffect(() => {
        axios.get("http://127.0.0.1:8000/api/getProfile/",
        {headers: {
            'Content-Type':'application/json',
              'Authorization':'Bearer ' + String(authTokens.access)
            }}
        ).then((res) => {
            setUsuario(res.data)
        }).catch(() => {
          alert("Algo fue mal")
        })
      }, [])



      const handleUpdate = async (id, value) => {
        return axios.put(`http://127.0.0.1:8000/api/updateProfile/${id}/`, value 
        ,{headers: {
            'Content-Type':'application/json',
              'Authorization':'Bearer ' + String(authTokens.access)


            }

        })
        .then((res) => {
            const { data } = res;
            const newBlogs = notes.map( b => {
                if (b.id === id){
                    return data;

                }
                return b;
            })
            setNotes(newBlogs);
        }).catch(() => {
            alert("Algo fue mal")
        })
    }

    const handleSaveChanges = async () => {
        await handleUpdate(record.id, {body: record.body});

        handleClose();
    }

    const handleClose = () => {
        setShow(false);
    }

    const handleChange = (e) => {
    setRecord({
        ...record,
        body: e.target.value
    })
    }


    return (
        <div>

        {usuario.map( u =>  {
                return  <div>

          {
            user.username === u.user &&
            <>
            <Link to={`/getProfile/${u.id}`}>
              <li>{u.user} | {u.id} </li>
            </Link>
            </>
        }

            </div>
    })}



            <p>You are logged to the home page!</p>
            <Button variant='dark' onClick={event =>  window.location.href='/crearPerfil'}>
                    Crear Perfil
             </Button>

             <Button variant='warning' onClick={event =>  window.location.href='/editarPerfil'}>
                    Editar Perfil
             </Button>


            <ul>
                {notes.map(note => (
                    <li key={note.id} >{note.body}</li>
                ))}
            </ul>

            <Modal show={show} onHide={handleClose}>

                <ModalHeader closeButton>
                    <Modal.Title>
                        Editar Post
                    </Modal.Title>
                </ModalHeader>

                <Modal.Body>
                    <FormControl value={record ? record.body : ""}
                    onChange={handleChange}/>
                </Modal.Body>
                <Modal.Footer>
                    <Button variant='dark' onClick={handleClose}>Cerrar </Button>

                    <Button variant='warning' onClick={handleSaveChanges}>Guardar</Button>
                </Modal.Footer>
                </Modal>



        </div>
    )
}

export default MiPerfil
