import Button from 'react-bootstrap/Button';
import React, { useState, useEffect }from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import Card from 'react-bootstrap/Card';
import ListGroup from "react-bootstrap/ListGroup"
import Modal from "react-bootstrap/Modal";
import ListGroupItem from 'react-bootstrap/esm/ListGroupItem';
import ModalHeader from 'react-bootstrap/esm/ModalHeader';
import { useContext } from "react";
import UserInfo from "../components/UserInfo";
import AuthContext from "../context/AuthContext";
import Form from 'react-bootstrap/Form';
import InputGroup from "react-bootstrap/InputGroup";
import FormControl from "react-bootstrap/FormControl";
import useAxios from "../utils/useAxios";
import BlogForm from './BlogForm';
import { FaEdit, FaTrashAlt } from 'react-icons/fa';

function EditProfile() {
  const { user } = useContext(AuthContext);

    let [notes, setNotes] = useState([])
    let {authTokens, logoutUser} = useContext(AuthContext)
    let [usuario, setUsuario] = useState([]);
    const [show, setShow] = useState(false);
    const [record, setRecord] = useState(null);
    const [blogs, setBlogs] = useState([]);


    const [body, setBody] = useState
    ("");

    const handleUpdate = async (id, value) => {
        return axios.put(`http://127.0.0.1:8000/api/updateProfile/${id}/`, value 
        ,{headers: {
            'Content-Type':'application/json',
              'Authorization':'Bearer ' + String(authTokens.access)


            }

        })
        .then((res) => {
            const { data } = res;
            const newBlogs = notes.map( b => {
                if (b.id === id){
                    return data;

                }
                return b;
            })
            setNotes(newBlogs);
        }).catch(() => {
            alert("Algo fue mal")
        })
    }






    const handleSubmit = e => {
        e.preventDefault();
        if (!body) {
            alert("Pon Algo para postear")
        }

        axios.post("http://127.0.0.1:8000/api/createProfile/", {
            body:body
        },
        {headers: {
            'Content-Type':'application/json',
              'Authorization':'Bearer ' + String(authTokens.access)


            }}



        ).then((res) => {
            setBody("");
            const { data } = res;
            setBlogs([
                ...blogs,
                data
            ]).catch(() => {
                alert("Algo fue mal")
            })
        })
        }

    const handleSaveChanges = async () => {
        await handleUpdate(record.id, {body: record.body});

        handleClose();
    }

    const handleClose = () => {
        setShow(false);
    }

    const handleChange = (e) => {
    setRecord({
        ...record,
        body: e.target.value
    })
    }



  return (
    <div>
    <h1>Hola</h1>
    <Form onSubmit={handleSubmit}>
            <InputGroup className='mb-3'>
                <FormControl 
                onChange={handleChange}
                value={body}
                type="text"
                placeholder='Agrega un Post!'/>
                <Button variant='dark' type='submit' onClick={event =>  window.location.href='/blogs'}>
                    Postear
                </Button>
            </InputGroup>
        </Form>


        <Modal show={show} onHide={handleClose}>

            <ModalHeader closeButton>
                <Modal.Title>
                    Editar Post
                </Modal.Title>
            </ModalHeader>

            <Modal.Body>
                <FormControl value={record ? record.body : ""}
                onChange={handleChange}/>
            </Modal.Body>
            <Modal.Footer>
                <Button variant='dark' onClick={handleClose}>Cerrar </Button>

                <Button variant='warning' onClick={handleSaveChanges}>Guardar</Button>
            </Modal.Footer>
        </Modal>



        </div>

);
}



export default EditProfile;
