import Button from 'react-bootstrap/Button';
import React, { useState, useEffect }from "react";

import axios from "axios";
import Card from 'react-bootstrap/Card';
import ListGroup from "react-bootstrap/ListGroup"
import Modal from "react-bootstrap/Modal";
import ListGroupItem from 'react-bootstrap/esm/ListGroupItem';
import ModalHeader from 'react-bootstrap/esm/ModalHeader';
import { useContext } from "react";
import UserInfo from "../components/UserInfo";
import AuthContext from "../context/AuthContext";
import Form from 'react-bootstrap/Form';
import InputGroup from "react-bootstrap/InputGroup";
import FormControl from "react-bootstrap/FormControl";
import useAxios from "../utils/useAxios";
import BlogForm from './BlogForm';


function Blogs() {
  let [notes, setNotes] = useState([])
  let {authTokens, logoutUser} = useContext(AuthContext)



  useEffect(() => {
    axios.get("http://127.0.0.1:8000/api/getBlog/", 
    {headers: {
        'Content-Type':'application/json',
          'Authorization':'Bearer ' + String(authTokens.access)
        }}
    ).then((res) => {
        setNotes(res.data)
    }).catch(() => {
      alert("Algo fue mal")
    })
  }, [])




  return (
    <ListGroup>
    {notes.map( b =>  {
        return <div>

        <ListGroupItem key={b.id} className="d-flex justify-content-between align-items-center">
        {b.body} | {b.user}
        <div>
        <FaEdit 
        onClick={() => {setRecord(b); setShow(true)}}
        style={{cursor: "pointer", marginRigth:"12px"}}/>

        &nbsp; &nbsp;

        <FaTrashAlt 
        onClick={() => {
            handleDelete(b.id);
        }}
        style={{cursor: "pointer"}}/>
        </div>

    </ListGroupItem>

        <Modal show={show} onHide={handleClose}>

            <ModalHeader closeButton>
                <Modal.Title>
                    Editar Post
                </Modal.Title>
            </ModalHeader>

            <Modal.Body>
                <FormControl value={record ? record.body : ""}
                onChange={handleChange}/>
            </Modal.Body>
            <Modal.Footer>
                <Button variant='dark' onClick={handleClose}>Cerrar </Button>

                <Button variant='warning' onClick={handleSaveChanges}>Guardar</Button>
            </Modal.Footer>
        </Modal>


    </div>
    } )}
</ListGroup>
);
}



export default Blogs;
